import React from "react";


function PostContainer({ postData }) {

    const postArray = postData.map((post) => {
        const commentArray = post.comments.map((comment) => {
            return (
                <li>{comment}</li>
            )
        })
        return (
            <li className="post" key={post.title} id={post.game}>
                <h3>{post.title}</h3>
                <p>{post.status}</p>
                <p>{post.description}</p>
                <p>{post.game}</p>
                <ul>
                    {commentArray}
                </ul>
                <form>
                    <input type="text" placeholder="Type a comment here!"></input>
                    <button></button>
                </form>
            </li>
        )
    })

    return (
        <ul>
            {postArray}
        </ul>
    )
}

export default PostContainer;
