import React from "react";


function PostContainer({ postData }) {


    const postArray = postData.map((post) => {
        const commentArray = post.comments.map((comment) => {


            function handleCommentSubmit (e) {
                e.preventDefault()
                fetch(`http://localhost:3000/posts/${post.id}`, {
                    method: "PATCH",
                    headers: { 'content-type': 'application/json' },
                    body: JSON.stringify({
                      comments: {comment}
                    })
                    .then(resp => resp.json())
                    .then(data => console.log(data))
                })
            }


            return (
                <li>{comment}</li>
            )
        })
        return (
            <li className="post" key={post.title} id={post.game}>
                <h3>{post.title}</h3>
                <p>{post.status}</p>
                <p>{post.description}</p>
                <p>{post.game}</p>
                <ul>
                    {commentArray}
                </ul>
                <form onSubmit={(e) => handleCommentSubmit(e)}>
                    <input type="text" name="comment" placeholder="Type a comment here!"></input>
                    <button>POST</button>
                </form>
            </li>
        )
    })

    return (
        <ul>
            {postArray}
        </ul>
    )
}

export default PostContainer;
