import React, {useState} from "react";


function PostContainer({ postData }) {
    const [newComment, setNewComment] = useState("")


    const postArray = postData.map((post) => {

        function handleCommentSubmit (e) {
            e.preventDefault()
            fetch(`http://localhost:3000/posts/${post.id}`, {
                method: "PATCH",
                headers: { 'content-type': 'application/json' },
                body: JSON.stringify({
                  comments: {newComment}
                })
            })
                .then(resp => resp.json())
                .then(data => setNewComment([data]))
        }


        const commentArray = post.comments.map((comment) => {


            return (
                <li>{comment}</li>
            )
        })
        return (
            <li className="post" key={post.title} id={post.game}>
                <h3>{post.title}</h3>
                <p>{post.status}</p>
                <p>{post.description}</p>
                <p>{post.game}</p>
                <ul>
                    {commentArray}
                </ul>
                <form onSubmit={(e) => handleCommentSubmit(e)}>
                    <input type="text" name="comment" placeholder="Type a comment here!" onChange={(e) => setNewComment(e.target.value)}></input>
                    <button>POST</button>
                </form>
            </li>
        )
    })

    return (
        <ul>
            {postArray}
        </ul>
    )
}

export default PostContainer;
